import React, { useEffect, useState, useRef } from "react";
import ScrollToBottom from "react-scroll-to-bottom";
// import AutoScroll from "@brianmcallister/react-auto-scroll";
import Moment from "react-moment";
import { makeStyles } from "@material-ui/core/styles";
import SendIcon from "@material-ui/icons/Send";
import { css } from "@emotion/css";

const ROOT_CSS = css({
  height: 600,
});

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
  chatSection: {
    width: "100%",
    // height: "80vh",
  },
  headBG: {
    backgroundColor: "#e0e0e0",
  },
  borderRight500: {
    borderRight: "1px solid #e0e0e0",
  },
  messageArea: {
    height: "70vh",
    overflowY: "auto",
  },
  from: {
    // width: "100%",
    textAlign: "right",
    fontSize: "8px",
    margin: "5px 0px 0px 25px",
  },

  messageTextSelf: {
    float: "right",
    background: "#683db8",
    color: "#FFF",
    padding: "9px",
    fontSize: "13px",
    borderRadius: "5px",
    marginBottom: "3px",
  },

  messageTextOpponent: {
    float: "left",
    background: "#ecf1f8",
    color: "#4a4a4a",
    padding: "9px",
    fontSize: "13px",
    borderRadius: "5px",
    marginBottom: "3px",
  },
});

function Chat({ socket, username, receiver, chatData }) {
  const classes = useStyles();
  const [currentMessage, setCurrentMessage] = useState("");
  const [messageList, setMessageList] = useState([]);

  const sendMessage = async () => {
    if (currentMessage !== "") {
      const messageData = {
        receiver: receiver,
        sender: username,
        message: currentMessage,
        time: new Date(),
      };

      await socket.emit("send_message", messageData);
      setMessageList((list) => [...list, messageData]);
      setCurrentMessage("");
    }
  };

  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  // useEffect(() => {
  //   scrollToBottom();
  // }, [messageList]);

  useEffect(() => {
    socket.on("receive_message", (data) => {
      setMessageList((list) => [...list, data]);
    });
    scrollToBottom();

    // setMessageList(chatData);
  }, [socket]);

  useEffect(() => {
    setMessageList(chatData);
  }, [chatData]);

  return (
    <div className="container">
      <div className="chat_box">
        <div className="head">
          <div className="user">
            <div className="avatar">
              <img src="https://picsum.photos/g/40/40" alt="test" />
            </div>
            <div className="name">{receiver}</div>
          </div>
        </div>
        <div className="body">
          {/* <ScrollToBottom> */}
          {messageList.map((messageContent, index) => {
            return (
              <div
                ref={messagesEndRef}
                className={
                  username === messageContent.sender ? "outgoing" : "incoming"
                }
                key={messageContent.message + index}
              >
                <div className="bubble">
                  <div className="d-inline">
                    <span>{messageContent.message}</span>
                    <span className="font-msg-time">
                      <Moment format="HH:mm" id="time">
                        {messageContent.time}
                      </Moment>
                    </span>
                  </div>
                </div>
              </div>
            );
          })}
          {/* </ScrollToBottom> */}

          {/* <div className="typing">
            <div className="bubble">
              <div className="ellipsis dot_1"></div>
              <div className="ellipsis dot_2"></div>
              <div className="ellipsis dot_3"></div>
            </div>
          </div> */}
        </div>
        <div className="foot">
          <input
            type="text"
            className="msg"
            placeholder="Type a message..."
            value={currentMessage}
            onChange={(event) => {
              setCurrentMessage(event.target.value);
            }}
            onKeyPress={(event) => {
              event.key === "Enter" && sendMessage();
            }}
          />
          <button type="submit" onClick={sendMessage}>
            <SendIcon></SendIcon>
          </button>
        </div>
      </div>
    </div>

    // <div className="chat-window">
    //   <div className="chat-header">
    //     <p>{receiver}</p>
    //   </div>
    //   <div className="chat-body">
    // <ScrollToBottom className="message-container">
    //   {messageList.map((messageContent, index) => {
    //     return (
    //       <div
    //         className="message"
    //         id={username === messageContent.sender ? "other" : "you"}
    //         key={messageContent.message + index}
    //       >
    //         <div>
    // <div className="message-content">
    //   <p>{messageContent.message}</p>
    // </div>
    //           <div className="message-meta">
    //             <Moment format="HH:mm" id="time">
    //               {messageContent.time}
    //             </Moment>

    //             <p id="author">{messageContent.sender}</p>
    //           </div>
    //         </div>
    //       </div>
    //     );
    //   })}
    // </ScrollToBottom>
    //   </div>
    //   <div className="chat-footer">
    //     <input
    //       type="text"
    // value={currentMessage}
    // placeholder="Hey..."
    // onChange={(event) => {
    //   setCurrentMessage(event.target.value);
    // }}
    // onKeyPress={(event) => {
    //   event.key === "Enter" && sendMessage();
    // }}
    //     />
    //     <button onClick={sendMessage}>&#9658;</button>
    //   </div>
    // </div>
  );
}

export default Chat;
