  messageTextSelf: {
    float: "right",
    background: "#683db8",
    color: "#FFF",
    padding: "9px",
    fontSize: "13px",
    borderRadius: "5px",
    marginBottom: "3px",
  },

  messageTextOpponent: {
    float: "left",
    background: "#ecf1f8",
    color: "#4a4a4a",
    padding: "9px",
    fontSize: "13px",
    borderRadius: "5px",
    marginBottom: "3px",
  },
});

function Chat({ socket, username, receiver, chatData }) {
  const classes = useStyles();
  const [currentMessage, setCurrentMessage] = useState("");
  const [messageList, setMessageList] = useState([]);

  const sendMessage = async () => {
    if (currentMessage !== "") {
      const messageData = {
        receiver: receiver,
        sender: username,
        message: currentMessage,
        time: new Date(),
      };

      await socket.emit("send_message", messageData);
      setMessageList((list) => [...list, messageData]);
      setCurrentMessage("");
    }
  };

  useEffect(() => {
    socket.on("receive_message", (data) => {
      setMessageList((list) => [...list, data]);
    });
    // setMessageList(chatData);
  }, [socket]);

  useEffect(() => {
    setMessageList(chatData);
  }, [chatData]);

  return (
    <div class="container">
      <div class="chat_box">
