import React, { useEffect, useState } from "react";
import ScrollToBottom from "react-scroll-to-bottom";
import Moment from "react-moment";

function Chat({ socket, username, receiver, chatData }) {
  const [currentMessage, setCurrentMessage] = useState("");
  const [messageList, setMessageList] = useState([]);

  const sendMessage = async () => {
    if (currentMessage !== "") {
      const messageData = {
        receiver: receiver,
        sender: username,
        message: currentMessage,
        time: new Date(),
      };

      await socket.emit("send_message", messageData);
      setMessageList((list) => [...list, messageData]);
      setCurrentMessage("");
    }
  };

  useEffect(() => {
    socket.on("receive_message", (data) => {
      setMessageList((list) => [...list, data]);
    });
    // setMessageList(chatData);
  }, [socket]);

  useEffect(() => {
    setMessageList(chatData);
  }, [chatData]);

  return (
    // <div className="chat-window">
    //   <div className="chat-header">
    //     <p>{receiver}</p>
    //   </div>
    //   <div className="chat-body">
    // <ScrollToBottom className="message-container">
    //   {messageList.map((messageContent, index) => {
    //     return (
    //       <div
    //         className="message"
    //         id={username === messageContent.sender ? "other" : "you"}
    //         key={messageContent.message + index}
    //       >
    //         <div>
    //           <div className="message-content">
    //             <p>{messageContent.message}</p>
    //           </div>
    //           <div className="message-meta">
    //             <Moment format="HH:mm" id="time">
    //               {messageContent.time}
    //             </Moment>

    //             <p id="author">{messageContent.sender}</p>
    //           </div>
    //         </div>
    //       </div>
    //     );
    //   })}
    // </ScrollToBottom>
    //   </div>
    //   <div className="chat-footer">
    //     <input
    //       type="text"
    //       value={currentMessage}
    //       placeholder="Hey..."
    // onChange={(event) => {
    //   setCurrentMessage(event.target.value);
    // }}
    // onKeyPress={(event) => {
    //   event.key === "Enter" && sendMessage();
    // }}
    //     />
    //     <button onClick={sendMessage}>&#9658;</button>
    //   </div>
    // </div>
    <div class="container clearfix">
      <div class="people-list" id="people-list">
        <div class="search">
          <input type="text" placeholder="search" />
          <i class="fa fa-search"></i>
        </div>
        <ul class="list">
          <li class="clearfix">
            <img
              src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/195612/chat_avatar_01.jpg"
              alt="avatar"
            />
            <div class="about">
              <div class="name">Vincent Porter</div>
              <div class="status">
                <i class="fa fa-circle online"></i> online
              </div>
            </div>
          </li>

          <li class="clearfix">
            <img
              src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/195612/chat_avatar_02.jpg"
              alt="avatar"
            />
            <div class="about">
              <div class="name">Aiden Chavez</div>
              <div class="status">
                <i class="fa fa-circle offline"></i> left 7 mins ago
              </div>
            </div>
          </li>

          <li class="clearfix">
            <img
              src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/195612/chat_avatar_03.jpg"
              alt="avatar"
            />
            <div class="about">
              <div class="name">Mike Thomas</div>
              <div class="status">
                <i class="fa fa-circle online"></i> online
              </div>
            </div>
          </li>

          <li class="clearfix">
            <img
              src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/195612/chat_avatar_04.jpg"
              alt="avatar"
            />
            <div class="about">
              <div class="name">Erica Hughes</div>
              <div class="status">
                <i class="fa fa-circle online"></i> online
              </div>
            </div>
          </li>

          <li class="clearfix">
            <img
              src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/195612/chat_avatar_05.jpg"
              alt="avatar"
            />
            <div class="about">
              <div class="name">Ginger Johnston</div>
              <div class="status">
                <i class="fa fa-circle online"></i> online
              </div>
            </div>
          </li>

          <li class="clearfix">
            <img
              src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/195612/chat_avatar_06.jpg"
              alt="avatar"
            />
            <div class="about">
              <div class="name">Tracy Carpenter</div>
              <div class="status">
                <i class="fa fa-circle offline"></i> left 30 mins ago
              </div>
            </div>
          </li>

          <li class="clearfix">
            <img
              src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/195612/chat_avatar_07.jpg"
              alt="avatar"
            />
            <div class="about">
              <div class="name">Christian Kelly</div>
              <div class="status">
                <i class="fa fa-circle offline"></i> left 10 hours ago
              </div>
            </div>
          </li>

          <li class="clearfix">
            <img
              src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/195612/chat_avatar_08.jpg"
              alt="avatar"
            />
            <div class="about">
              <div class="name">Monica Ward</div>
              <div class="status">
                <i class="fa fa-circle online"></i> online
              </div>
            </div>
          </li>

          <li class="clearfix">
            <img
              src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/195612/chat_avatar_09.jpg"
              alt="avatar"
            />
            <div class="about">
              <div class="name">Dean Henry</div>
              <div class="status">
                <i class="fa fa-circle offline"></i> offline since Oct 28
              </div>
            </div>
          </li>

          <li class="clearfix">
            <img
              src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/195612/chat_avatar_10.jpg"
              alt="avatar"
            />
            <div class="about">
              <div class="name">Peyton Mckinney</div>
              <div class="status">
                <i class="fa fa-circle online"></i> online
              </div>
            </div>
          </li>
        </ul>
      </div>

      <div class="chat">
        <div class="chat-header clearfix">
          <img
            src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/195612/chat_avatar_01_green.jpg"
            alt="avatar"
          />

          <div class="chat-about">
            <div class="chat-with">Chat with Vincent Porter</div>
            <div class="chat-num-messages">already 1 902 messages</div>
          </div>
          <i class="fa fa-star"></i>
        </div>

        <div class="chat-history">
          <ul>
            <li class="clearfix">
              <div class="message-data align-right">
                <span class="message-data-time">10:10 AM, Today</span> &nbsp;
                &nbsp;
                <span class="message-data-name">Olia</span>{" "}
                <i class="fa fa-circle me"></i>
              </div>
              <div class="message other-message float-right">
                Hi Vincent, how are you? How is the project coming along?
              </div>
            </li>

            <li>
              <div class="message-data">
                <span class="message-data-name">
                  <i class="fa fa-circle online"></i> Vincent
                </span>
                <span class="message-data-time">10:12 AM, Today</span>
              </div>
              <div class="message my-message">
                Are we meeting today? Project has been already finished and I
                have results to show you.
              </div>
            </li>

            <li class="clearfix">
              <div class="message-data align-right">
                <span class="message-data-time">10:14 AM, Today</span> &nbsp;
                &nbsp;
                <span class="message-data-name">Olia</span>{" "}
                <i class="fa fa-circle me"></i>
              </div>
              <div class="message other-message float-right">
                Well I am not sure. The rest of the team is not here yet. Maybe
                in an hour or so? Have you faced any problems at the last phase
                of the project?
              </div>
            </li>

            <li>
              <div class="message-data">
                <span class="message-data-name">
                  <i class="fa fa-circle online"></i> Vincent
                </span>
                <span class="message-data-time">10:20 AM, Today</span>
              </div>
              <div class="message my-message">
                Actually everything was fine. I'm very excited to show this to
                our team.
              </div>
            </li>

            <li>
              <div class="message-data">
                <span class="message-data-name">
                  <i class="fa fa-circle online"></i> Vincent
                </span>
                <span class="message-data-time">10:31 AM, Today</span>
              </div>
              <i class="fa fa-circle online"></i>
              <i class="fa fa-circle online" style="color: #AED2A6"></i>
              <i class="fa fa-circle online" style="color:#DAE9DA"></i>
            </li>
          </ul>
        </div>

        <div class="chat-message clearfix">
          <textarea
            name="message-to-send"
            id="message-to-send"
            placeholder="Type your message"
            rows="3"
          ></textarea>
          <i class="fa fa-file-o"></i> &nbsp;&nbsp;&nbsp;
          <i class="fa fa-file-image-o"></i>
          <button>Send</button>
        </div>
      </div>
    </div>
  );
}

export default Chat;
