import "./App.css";
import io from "socket.io-client";
import { useState } from "react";
import Chat from "./chat";

const socket = io.connect("http://localhost:3001");

function App() {
  const [username, setUsername] = useState("");
  const [receiver, setReceiver] = useState("");
  const [showChat, setShowChat] = useState(false);

  const joinRoom = () => {
    (async () => {
      const rawResponse = await fetch("http://localhost:3001/get_messages", {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ a: 1, b: "Textual content" }),
      });
      const content = await rawResponse.json();

      console.log(content);
    })();
    // const url = "http://localhost:3001/get_messages";

    // let data = {
    //   sender: "khai",
    //   receiver: "yenlin",
    // };

    // var request = new Request(url, {
    //   method: "POST",
    //   body: data,
    //   headers: new Headers(),
    // });

    // fetch(request).then(function () {
    //   // Handle response we get from the API
    // });
    // let data = {
    //   sender: "khai",
    //   receiver: "yenlin",
    // };
    // fetch("http://localhost:3001/get_messages", {
    //   method: "post",
    //   headers: new Headers(),
    //   body: data,
    // })
    //   .then((res) => res.json())
    //   .then((res) => console.log(res));

    return false;
    if (username !== "" && receiver !== "") {
      socket.emit("join_room", receiver);

      setShowChat(true);
    }
  };

  return (
    <div className="App">
      {!showChat ? (
        <div className="joinChatContainer">
          <h3>Join A Chat</h3>
          <input
            type="text"
            placeholder="John..."
            onChange={(event) => {
              setUsername(event.target.value);
            }}
          />
          <input
            type="text"
            placeholder="Receiver Name..."
            onChange={(event) => {
              setReceiver(event.target.value);
            }}
          />
          <button onClick={joinRoom}>Join A Room</button>
        </div>
      ) : (
        <Chat socket={socket} username={username} receiver={receiver} />
      )}
    </div>
  );
}

export default App;
